import { SamsaraClient } from "../../src/Client";
import { mockServerPool } from "../mock-server/MockServerPool";

describe("MessagesClient", () => {
    test("V1getMessages", async () => {
        const server = mockServerPool.createServer();
        const client = new SamsaraClient({
            maxRetries: 0,
            token: "test",
            version: "2025-06-11",
            environment: server.baseUrl,
        });

        const rawResponseBody = {
            data: [
                {
                    driverId: 555,
                    isRead: true,
                    sender: { name: "John Doe", type: "dispatch" },
                    sentAtMs: 1462881998034,
                    text: "This is a message.",
                },
            ],
        };
        server.mockEndpoint().get("/v1/fleet/messages").respondWith().statusCode(200).jsonBody(rawResponseBody).build();

        const response = await client.messages.v1GetMessages();
        expect(response).toEqual({
            data: [
                {
                    driverId: 555,
                    isRead: true,
                    sender: {
                        name: "John Doe",
                        type: "dispatch",
                    },
                    sentAtMs: 1462881998034,
                    text: "This is a message.",
                },
            ],
        });
    });

    test("V1createMessages", async () => {
        const server = mockServerPool.createServer();
        const client = new SamsaraClient({
            maxRetries: 0,
            token: "test",
            version: "2025-06-11",
            environment: server.baseUrl,
        });
        const rawRequestBody = { driverIds: [111, 222, 333], text: "This is a message." };
        const rawResponseBody = { data: [{ driverId: 555, text: "This is a message." }] };
        server
            .mockEndpoint()
            .post("/v1/fleet/messages")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
            .jsonBody(rawResponseBody)
            .build();

        const response = await client.messages.v1CreateMessages({
            driverIds: [111, 222, 333],
            text: "This is a message.",
        });
        expect(response).toEqual({
            data: [
                {
                    driverId: 555,
                    text: "This is a message.",
                },
            ],
        });
    });
});
